//Convierte un String con valor "10.5" a float usando Float.parseFloat().
public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String numeroDecimal = "10.5";
		float decimalConvertido = Float.parseFloat(numeroDecimal);
		System.out.println(decimalConvertido);
	}

}
